import express from 'express';
import bodyParser from 'body-parser';
import dotenv from 'dotenv';
import authRouter from './routes/authRoutes';
import cors from 'cors';
import characterRouter from './routes/characterRoutes';
import classRouter from './routes/classRoutes';
import subraceRouter from './routes/subraceRoutes';
import raceRouter from './routes/raceRoutes';
import itemRouter from './routes/itemRoutes';
import subclassRouter from './routes/subclassRoutes';
import abilityRouter from './routes/abilityRoutes';
import skillRouter from './routes/skillRoutes';
import languageRouter from './routes/languageRoutes';
import spellRouter from './routes/spellRoutes';
import fieldRouter from './routes/fieldRoutes';
import campaignRouter from './routes/campaignRoutes';
dotenv.config();
const app = express();
app.use(cors());
app.use(bodyParser.json());
app.use('/api/auth', authRouter);
app.use('/api/characters', characterRouter);
app.use('/api/races', raceRouter);
app.use('/api/subraces', subraceRouter);
app.use('/api/classes', classRouter);
app.use('/api/subclasses', subclassRouter);
app.use('/api/items', itemRouter);
app.use('/api/abilities', abilityRouter);
app.use('/api/skills', skillRouter);
app.use('/api/languages', languageRouter);
app.use('/api/spells', spellRouter);
app.use('/api/fields', fieldRouter);
app.use('/api/campaigns', campaignRouter);
const PORT = process.env.PORT || 5000;
app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
});
